import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as s,g as n,a as e,d as a}from"./app-df33d186.js";const c={},r=e("h1",{id:"喜讯-丁兴号教授和曾德炉副教授获得国家自然科学基金面上项目",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#喜讯-丁兴号教授和曾德炉副教授获得国家自然科学基金面上项目","aria-hidden":"true"},"#"),a(" 喜讯：丁兴号教授和曾德炉副教授获得国家自然科学基金面上项目！")],-1),_=e("p",null,"2015年度国家自然科学基金揭晓，实验室丁兴号教授、曾德炉副教授均获得面上项目！",-1),d=e("p",null,"国家自然科学基金作为我国支持基础研究的主渠道之一，面向全国，重点资助具有良好研究条件、研究实力的高等院校中和科研机构中的研究人员。20世纪80年代初，为推动我国科技体制改革，变革科研经费拨款方式，中国科学院89位院士（学部委员）致函党中央、国务院，建议设立面向全国的自然科学基金，得到党中央、国务院的首肯。随后，在邓小平同志的亲切关怀下，国务院于1986年2月14日批准成立国家自然科学基金委员会。自然科学基金坚持支持基础研究，逐渐形成和发展了由研究项目、人才项目和环境条件项目三大系列组成的资助格局。二十多年来，自然科学基金在推动我国自然科学基础研究的发展，促进基础学科建设，发现、培养优秀科技人才等方面取得了巨大成绩。自然科学基金按照资助类别可分为面上项目、重点项目、重大项目、重大研究计划、国家杰出青年科学基金、海外、港澳青年学者合作研究基金、创新研究群体科学基金、国家基础科学人才培养基金、专项项目、联合资助基金项目以及国际（地区）合作与交流项目等。（来源：百度百科）",-1),l=e("p",null,"目前，我实验室所有老师均有主持基金项目，充分调动资源培养博士生和研究生，增强了实验室的科研竞争力，实验室的科研能力也日益提高。",-1);function i(h,m){return o(),s("div",null,[r,_,n(" more "),d,l])}const p=t(c,[["render",i],["__file","news20150824.html.vue"]]);export{p as default};
