import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o,c as r,g as s,a as e,d as n}from"./app-a68fcec1.js";const a="/news/newsimage/20170811.png",c={},i=e("h1",{id:"喜讯-我实验室硕士生杨骏锋、博士生傅雪阳斩获iccv",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#喜讯-我实验室硕士生杨骏锋、博士生傅雪阳斩获iccv","aria-hidden":"true"},"#"),n(" 喜讯：我实验室硕士生杨骏锋、博士生傅雪阳斩获ICCV")],-1),C=e("p",null,[n("2017年国际计算机视觉会议（ICCV2017）论文录用名单近日发布，我实验室硕士生杨骏锋、博士生傅雪阳（共同第一作者）在超分辨率贝叶斯方面的研究论文“PanNet:A"),e("br"),n(" deep network architecture for pan-"),e("br"),n(" sharpening”被该会议录用。这也是我实验室硕士生第二次作为第一作者在国际计算机视觉顶级会议上发表论文。")],-1),_=e("figure",null,[e("img",{src:a,alt:"",tabindex:"0",loading:"lazy"}),e("figcaption")],-1),l=e("p",null,"杨骏锋、傅雪阳分别系我校信息科学与技术学院通信工程系智能数据分析与处理实验室2015级硕士生及2014级博士生，该篇论文的完成，得到了丁兴号教授（通讯作者）的悉心指导，在超分辨率贝叶斯方面的发展起到了显著的作用。",-1),d=e("p",null,[n("ICCV 的全称是 IEEE International Conference on Computer"),e("br"),n(" Vision，即国际计算机视觉大会，由IEEE主办，与计算机视觉模式识别会议（CVPR）和欧洲计算机视觉会议（ECCV）并称计算机视觉方向的三大顶级会议。ICCV"),e("br"),n(" 每两年举办一次，2017年ICCV将于10月22--"),e("br"),n(" 29日在意大利威尼斯举办。ICCV被中国计算机学会（CCF）列为A类会议。根据CCF分类标准，“A类会议指国际上极少数的顶级学术会议，鼓励我国学者去突破”。")],-1),h=e("p",null,"ICCV2017总投稿量达2000多篇，最终录取600多篇，录取率约为28%。此次杨骏锋、傅雪阳在ICCV2017会议上取得的成果实现了我校在计算机视觉顶级会议上的又一突破。这也是我实验室科研建设的重要成果之一。同时标志着我实验室在计算机视觉领域方面先进的科研水平，在计算机视觉和模式识别领域不断产生出国际先进水平的成果。",-1);function p(m,u){return o(),r("div",null,[i,C,s(" more "),_,l,d,h])}const I=t(c,[["render",p],["__file","news20170811.html.vue"]]);export{I as default};
