import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r as a,o as r,c as s,g as c,a as e,d as n,b as i}from"./app-368692da.js";const l={},_=e("h1",{id:"喜讯-我实验室硕士生樊志文、吴华峰论文被acm-mm录用",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#喜讯-我实验室硕士生樊志文、吴华峰论文被acm-mm录用","aria-hidden":"true"},"#"),n(" 喜讯：我实验室硕士生樊志文、吴华峰论文被ACM MM录用")],-1),u=e("p",null,[n("通信工程系2016级硕士研究生樊志文、吴华峰作为共同第一作者，在黄悦副教授和丁兴号教授的指导下，发表题为“Residual-Guide Feature"),e("br"),n(" Fusion Network for Single Image Deraining 作者：Zhiwen Fan, Huafeng"),e("br"),n(" Wu(共同一作),Xueyang Fu, Yue Huang, Xinghao Ding.”的论文。")],-1),d=e("p",null,"近年来，图像去雨任务广泛吸引计算机视觉领域的注意力。目前以深度学习为主流的单幅图像去雨模型存在复杂度较高并且没有考虑雨线稀密程度的问题。本文提出了一种基于残差引导的特征融合轻量化图像去雨深度学习模型。该模型包含多个去雨子模块，可以渐近学习出清晰的雨线。相对于已有的方法，本文提出的残差引导方案可以视为一种基于注意力机制的浅层与深层特征相融合方案，该方案使得深层模块可以有效抑制不相关的图像背景，提高学习过程的速度和雨线估计的精度。相关公开数据集上的仿真与真实数据实验表明本文所提方法具有明显的优势，且该模型可以拓展至去噪等图像处理任务。",-1),m={href:"https://zhiwenfan.github.io/",target:"_blank",rel:"noopener noreferrer"},h=e("br",null,null,-1);function f(p,g){const o=a("ExternalLinkIcon");return r(),s("div",null,[_,u,c(" more "),d,e("p",null,[e("a",m,[n(" 樊志文 "),i(o)]),n(" ，厦门大学通信工程系2016级硕士研究生，导师丁兴号教授，已以共同一作在AAAI"),h,n(" (CCF-A类)发表论文一篇，一篇一作论文被计算机视觉领域三大顶会之一的ECCV2018录用。")])])}const C=t(l,[["render",f],["__file","news20180702.html.vue"]]);export{C as default};
