import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as r,c as o,g as a,a as e,d as n,e as i}from"./app-e20856bd.js";const s="/news/newsimage/20190225.png",c={},p=e("h1",{id:"捷报-实验室3篇论文被cvpr2019接收",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#捷报-实验室3篇论文被cvpr2019接收","aria-hidden":"true"},"#"),n(" 捷报！实验室3篇论文被CVPR2019接收")],-1),d=e("p",null,"今日，国际计算机视觉与模式识别会议（CVPR）发布2019年会议录取榜单，我实验室3篇论文被大会接收！",-1),_=i('<figure><img src="'+s+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>第一篇论文是&quot;Progressive Feature Alignment for Unsupervised Domain<br> Adaptation&quot;，由我实验室研究生二年级学生陈超奇、谢伟平与腾讯AI Lab 合作发表；第二篇论文是&quot;A Variation Pan-<br> Sharpening with Local Gradient Constraints&quot;, 由傅雪阳博士，二年级研究生林子煌同学等发表。第三篇文章是&quot;Look<br> More Than Once: An Accurate Detector for Text of Arbitrary<br> Shapes&quot;，由实验室研究生三年级学生梁柏荣和百度共同合作完成。</p><p>国际计算机视觉与模式识别会议（CVPR）是计算机视觉和模式识别领域国际顶级会议之一。在2018年发布的谷歌学术影响力（Google Scholar<br> Metrics，GSM）排名中，CVPR排名全球所有学科（期刊与会议均参与排序）的第20位，在所有电子、计算机、信息类学科专业中排名最高，大会录用的论文代表着在计算机视觉与模式识别领域当今世界最先进水平。CVPR有着严苛的录用标准，据官方统计，今年的录用率仅为25%。在各种学术会议统计中，CVPR被认为有着很强的影响力和很高的排名。目前在中国计算机学会（CCF）推荐的国际学术会议列表中，CVPR被列为人工智能领域的A类会议。</p><p>更多关于论文的详细内容将会在接下来的更新中持续公布。</p><p>厦门大学SmartDSP实验室是一所专注于研究机器学习、模式识别、智能数据处理以及医学医疗影像处理与分析的团队。陈超奇和谢伟平曾在腾讯AI<br> LAB机器学习组实习，梁柏荣2019年毕业后前往百度公司工作。</p>',5);function l(u,m){return r(),o("div",null,[p,d,a(" more "),_])}const g=t(c,[["render",l],["__file","news20190225.html.vue"]]);export{g as default};
