import{_ as o}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as a,c as i,a as e,d as n,b as s,g as c,e as l}from"./app-a68fcec1.js";const I="/news/newsimage/2019022601.jpg",_="/news/newsimage/2019022602.jpg",d={},p=e("h1",{id:"牛-我实验室博士生孙立言、硕士生樊志文论文被ipmi接收",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#牛-我实验室博士生孙立言、硕士生樊志文论文被ipmi接收","aria-hidden":"true"},"#"),n(" 牛！我实验室博士生孙立言、硕士生樊志文论文被IPMI接收")],-1),g={href:"https://arxiv.org/abs/1805.02165",target:"_blank",rel:"noopener noreferrer"},m=e("br",null,null,-1),f=e("br",null,null,-1),M=l('<figure><img src="'+I+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>在大数据时代对MRI数据的快速获取和自动分析的需求逐渐增长。虽然压缩感知磁共振成像(即CS-<br> MRI)已经被广泛研究并通过减少采集k空间的数据加速成像过程，但是在目前的CS-MRI领域的应用中，分割任务常常被忽视。在文中，作者测试了CS-<br> MRI在自动分割模型里的效果并且提出一个统一的深度神经网络框架-SegNetMRI，然后把这个框架应用到CS-<br> MRI重建和分割问题中来。SegNetMRI的构建基于MRI重建子网络和MRI分割子网络，其中重建子网络由几个级联起来的编码解码单元构成，分割子网络有同样的编解码结构。两个子网络需要预训练并且在共享编码器的情况下进行权重的微调。每个模块的分割结果通过卷积融合成最终分割结果。通过实验证明我们的模型在压缩感知的情况下可以提升重建和分割的性能。</p><figure><img src="'+_+'" alt="" tabindex="0" loading="lazy"><figcaption></figcaption></figure><p>国际医学影像信息处理会议（International Conference on Information Processing in Medical<br> Imaging，IPMI）是医学影像分析最顶级的会议之一（另一为MICCAI），两年召开一次，且限定人数为120左右，文章也只接受60多篇。<br> IPMI的会议设计是一切学术为先，接收的文章主要偏重方法的创新，理论性强，比较晦涩难懂；因此要彻底理解某篇文章需要时间充分交流。为此，IPMI允许在oral<br> talk之后无时间限制提问，任何人可以向讲者提出任何问题直到没有问题为止，这也使得IPMI被公认为医学影像分析领域最具特色的会议。</p><p>厦门大学SmartDSP实验室是一所专注于研究机器学习、模式识别、智能数据处理以及医学医疗影像处理与分析的团队。本篇论文由丁兴号，黄悦，John<br> Paisley三位老师共同指导完成。</p>',5);function h(u,b){const t=r("ExternalLinkIcon");return a(),i("div",null,[p,e("p",null,[n("今日，实验室博士生孙立言，硕士生樊志文共同一作完成的论文 "),e("a",g,[n(" “Joint CS-MRI Reconstruction and Segmentation"),m,n(" with a Unified Deep Network” "),s(t)]),f,n(" 被IPMI接收，这是我院第一篇被IPMI接收的论文。")]),c(" more "),M])}const x=o(d,[["render",h],["__file","news20190226.html.vue"]]);export{x as default};
