import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{r,o as s,c as a,a as e,d as n,b as c,g as i}from"./app-df33d186.js";const l="/news/newsimage/2019061401.jpg",_={},d=e("h1",{id:"喜讯-实验室孙立言、樊志文合作论文被tip接收",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#喜讯-实验室孙立言、樊志文合作论文被tip接收","aria-hidden":"true"},"#"),n(" 喜讯，实验室孙立言、樊志文合作论文被TIP接收")],-1),h={href:"https://arxiv.org/abs/1804.03596",target:"_blank",rel:"noopener noreferrer"},m=e("br",null,null,-1),p=e("br",null,null,-1),u=e("p",null,"在多对比度磁共振成像中，压缩感知理论可以通过对每个对比度采样更少的数据来加速成像过程。传统的基于优化的方法受限于：耗时的优化过程，“较浅”的模型很难编码MRI数据中丰富的模式以及较强的假设。在本文中，我们首次提出多对比度MRI重建领域的深度模型。我们通过特征共享单元实现了信息共享，极大地降低了模型所需的参数。特征共享单元包含有一个数据保真单元来组成一个推理模块。这些推理模块以级联的方式组合起来并且在不同的模块间引入了密集连接，密集连接策略可以实现在不同深度的模型中更有效率的传递。通过我们在不同多对比度MRI数据集上的实验表明，我们所提的模型在效率和准确度上均超越了目前性能最优的单对比度重建方法和多对比度重建方法。此外，通过将不同方法重建出的结果送入后续自动分割模型进行分析，我们的方法可以为分割任务带来更多的性能增益。此外，我们所提出的模型在非配准的情况下也能展现出鲁棒性，体现出我们模型在实际MRI应用中的潜力。",-1),f=e("figure",null,[e("img",{src:l,alt:"",tabindex:"0",loading:"lazy"}),e("figcaption")],-1),I=e("p",null,[n("IEEE Transactions on Image Processing（IEEE"),e("br"),n(" TIP）是Q1区期刊，属于中国计算机学会（CCF）推荐的计算机图形学与多媒体领域A类期刊，是图像处理及计算机视觉领域公认的国际顶级期刊，侧重图像处理的前沿理论与方法。最新的2019年影响因子为6.79。")],-1),g=e("p",null,[n("厦门大学SmartDSP实验室是一所专注于研究机器学习、模式识别、智能数据处理以及医学医疗影像处理与分析的团队。本篇论文由丁兴号，黄悦，John"),e("br"),n(" Paisley三位老师共同指导完成。孙立言博士现在在美国常青藤联盟美国哥伦比亚大学电子与计算机工程系进行为期一年的交流访问。")],-1);function b(x,E){const o=r("ExternalLinkIcon");return s(),a("div",null,[d,e("p",null,[n("今日，由我实验室博士生孙立言，硕士生樊志文共同合作的论文 "),e("a",h,[n(" “A Deep Information Sharing Network for Multi-"),m,n(" contrast Compressed Sensing MRI Reconstruction”"),p,c(o)]),n(" 被TIP接收。")]),i(" more "),u,f,I,g])}const P=t(_,[["render",b],["__file","news20190614.html.vue"]]);export{P as default};
